import{_ as t,C as a,o as s,c as i,H as o,Q as n}from"./chunks/framework.QBCT-Upj.js";const w=JSON.parse('{"title":"Data Flow Diagram","description":"","frontmatter":{"prev":false,"next":false},"headers":[],"relativePath":"content/gist/business-analysis/tools-techniques/data-flow-diagram.md","filePath":"content/gist/business-analysis/tools-techniques/data-flow-diagram.md"}'),r={name:"content/gist/business-analysis/tools-techniques/data-flow-diagram.md"},l=n("",10);function d(h,c,u,m,p,g){const e=a("QuizJS");return s(),i("div",null,[l,o(e,{quizData:[{id:4043,question:"A business analyst is trying to visually represent how data moves through a new customer onboarding process in an organization. Which tool would be most effective for illustrating the flow of data between various entities and processes?<br>",choices:[{key:"A",text:"Data Flow Diagram (DFD)"},{key:"B",text:"Gantt Chart"},{key:"C",text:"Balance Scorecard"},{key:"D",text:"Organizational Chart"}],answer:"A",explanation:"A Data Flow Diagram (DFD) is designed to represent how data moves through various processes and systems, making it an ideal tool for visually illustrating the flow of data in a customer onboarding process.<br><br>"},{id:4044,question:"In a Data Flow Diagram, what do the arrows represent?<br>",choices:[{key:"A",text:"Hierarchical relationships between departments"},{key:"B",text:"The direction of data movement"},{key:"C",text:"Time required for each process"},{key:"D",text:"Cost associated with data processing"}],answer:"B",explanation:"In a Data Flow Diagram, arrows are used to show the direction of data movement, indicating how data flows to and from processes, data stores, and external entities.<br><br>"},{id:4045,question:"What is the primary purpose of representing external entities in a Data Flow Diagram?<br>",choices:[{key:"A",text:"To indicate actors or systems that interact with the process from outside the system boundary"},{key:"B",text:"To show the internal staff involved in the process"},{key:"C",text:"To display the financial stakeholders of the project"},{key:"D",text:"To represent the technical infrastructure of the system"}],answer:"A",explanation:"External entities in a Data Flow Diagram represent actors or systems that interact with the process but lie outside the defined system's boundary, highlighting their role in data interaction.<br><br>"},{id:4046,question:"How does a Data Flow Diagram assist in the requirements gathering process?<br>",choices:[{key:"A",text:"By providing a timeline for project deliverables"},{key:"B",text:"By showing the flow of data, aiding in understanding how data requirements are met"},{key:"C",text:"By allocating budget for data-related activities"},{key:"D",text:"By assigning roles and responsibilities for data management"}],answer:"B",explanation:"A Data Flow Diagram assists in the requirements gathering process by providing a visual representation of the flow of data. This helps stakeholders understand how data requirements are met and how data moves through the system.<br><br>"},{id:4047,question:"What is a limitation of a Data Flow Diagram in terms of data analysis?<br>",choices:[{key:"A",text:"It does not indicate the timing or sequence of data movement"},{key:"B",text:"It cannot show the flow of data between processes"},{key:"C",text:"It is unable to represent data storage"},{key:"D",text:"It does not include external entities"}],answer:"A",explanation:"A limitation of a Data Flow Diagram is that it does not indicate the timing, sequence, or control factors affecting the data flow, focusing only on the stateless flow of data itself.<br>"}]},null,8,["quizData"])])}const y=t(r,[["render",d]]);export{w as __pageData,y as default};
