import{_ as t,C as o,o as i,c as n,H as a,Q as s}from"./chunks/framework.QBCT-Upj.js";const g=JSON.parse('{"title":"Definition Of Done (DoD)","description":"","frontmatter":{"prev":false,"next":false},"headers":[],"relativePath":"content/gist/business-analysis/tools-techniques/definition-of-done-dod.md","filePath":"content/gist/business-analysis/tools-techniques/definition-of-done-dod.md"}'),r={name:"content/gist/business-analysis/tools-techniques/definition-of-done-dod.md"},l=s("",12);function c(d,h,p,u,f,m){const e=o("QuizJS");return i(),n("div",null,[l,a(e,{quizData:[{id:4388,question:"A project team is working on a new software feature and wants to ensure that all aspects of the work are completed to a standard acceptable to the business stakeholders. Which concept should they establish to determine when the feature is ready for acceptance?<br>",choices:[{key:"A",text:"Work Breakdown Structure (WBS)"},{key:"B",text:"Definition of Done (DoD)"},{key:"C",text:"Project Charter"},{key:"D",text:"Risk Management Plan"}],answer:"B",explanation:"The Definition of Done (DoD) is a set of mutually agreed-upon conditions that a team commits to meet before a work item is considered complete and ready for acceptance by business stakeholders, making it the appropriate concept to determine when a feature is ready.<br>"},{id:4389,question:"In the context of Agile project management, what does the Definition of Done (DoD) typically include at the User Story level?<br>",choices:[{key:"A",text:"A list of all project stakeholders"},{key:"B",text:"A detailed project timeline"},{key:"C",text:"Acceptance criteria and other conditions for each user story"},{key:"D",text:"The total budget for the user story"}],answer:"C",explanation:"At the User Story level, the Definition of Done (DoD) typically includes acceptance criteria and other conditions that need to be fulfilled for the user story to be considered complete.<br>"},{id:4390,question:"How can the Definition of Done (DoD) evolve throughout the lifecycle of a project?<br>",choices:[{key:"A",text:"It remains constant and does not change"},{key:"B",text:"It evolves as the team gains insights or the project scope changes"},{key:"C",text:"It becomes less detailed and more general"},{key:"D",text:"It is only defined at the beginning and end of the project"}],answer:"B",explanation:"The Definition of Done (DoD) is not static and can evolve over time as the team gains more insights or as the project scope changes, ensuring continued alignment with project objectives and stakeholder expectations.<br>"},{id:4391,question:"What role does the Definition of Done (DoD) play in the solution evaluation phase of a project?<br>",choices:[{key:"A",text:"It provides a baseline for the project budget"},{key:"B",text:"It becomes a crucial input when evaluating the readiness of a solution for release"},{key:"C",text:"It outlines the roles and responsibilities of project team members"},{key:"D",text:"It schedules the tasks necessary for solution implementation"}],answer:"B",explanation:"The Definition of Done (DoD), particularly when integrated with acceptance criteria, serves as a crucial input in the solution evaluation phase, helping to determine the readiness of a solution for release.<br>"},{id:4392,question:"When a project item meets the criteria established in the Definition of Done (DoD), how is this typically reflected in project management tools?<br>",choices:[{key:"A",text:"The item is marked as 'in progress'"},{key:"B",text:"The item is marked as 'done' or 'complete'"},{key:"C",text:"The item's budget allocation is increased"},{key:"D",text:"The item's deadline is extended"}],answer:"B",explanation:"Once a project item meets the Definition of Done (DoD) criteria, it is typically marked as 'done' or 'complete' in project management tools, such as project plans, requirements management tools, or Kanban boards.<br>"}]},null,8,["quizData"])])}const b=t(r,[["render",c]]);export{g as __pageData,b as default};
