import{_ as t,C as a,o as s,c as n,H as i,Q as o}from"./chunks/framework.QBCT-Upj.js";const y=JSON.parse('{"title":"Impact Analysis","description":"","frontmatter":{"prev":false,"next":false},"headers":[],"relativePath":"content/gist/business-analysis/tools-techniques/impact-analysis.md","filePath":"content/gist/business-analysis/tools-techniques/impact-analysis.md"}'),r={name:"content/gist/business-analysis/tools-techniques/impact-analysis.md"},c=o("",18);function l(h,p,m,d,u,g){const e=a("QuizJS");return s(),n("div",null,[c,i(e,{quizData:[{id:6766,question:"When a project team is considering a change to their project, and the business analyst needs to assess the impact of this change on existing requirements, which tool is most effective for this purpose?<br>",choices:[{key:"A",text:"SWOT Analysis"},{key:"B",text:"Risk Register"},{key:"C",text:"Traceability Matrix"},{key:"D",text:"Gantt Chart"}],answer:"C",explanation:"A traceability matrix is an effective tool for assessing the impact of a proposed change on existing requirements, as it links requirements to their origins and traces them throughout the project lifecycle.<br><br>"},{id:6767,question:"In the context of Impact Analysis, what is the significance of conducting a conflict assessment?<br>",choices:[{key:"A",text:"To determine financial conflicts in the project budget"},{key:"B",text:"To identify and resolve conflicts between the proposed change and existing requirements"},{key:"C",text:"To assign conflicting tasks to different team members"},{key:"D",text:"To schedule conflicting project activities"}],answer:"B",explanation:"The significance of conducting a conflict assessment in Impact Analysis is to identify and resolve any conflicts between the proposed change and existing or baselined requirements, ensuring the integrity of the requirements baseline.<br><br>"},{id:6768,question:"How does Impact Analysis affect ongoing business analysis activities in a project?<br>",choices:[{key:"A",text:"It typically results in the immediate completion of all activities"},{key:"B",text:"It evaluates how the proposed change will affect these activities"},{key:"C",text:"It cancels all ongoing business analysis activities"},{key:"D",text:"It allocates additional budget to business analysis activities"}],answer:"B",explanation:"Impact Analysis involves evaluating how a proposed change will affect ongoing business analysis activities, including work that has been completed and approved, as well as work still in progress.<br><br>"},{id:6769,question:"Why is it important for a business analyst to collaborate with the project manager during Impact Analysis?<br>",choices:[{key:"A",text:"To assess the impact of the change on various subsidiary plans within the project management plan"},{key:"B",text:"To assign project management tasks to the business analyst"},{key:"C",text:"To create a new project management plan"},{key:"D",text:"To evaluate the project manager's performance"}],answer:"A",explanation:"Collaboration between the business analyst and the project manager is important during Impact Analysis to properly assess the impact of the change on various subsidiary plans within the project management plan, capturing both project and product viewpoints.<br><br>"},{id:6770,question:"What is the final stage in the Impact Analysis process?<br>",choices:[{key:"A",text:"Immediately implementing the change"},{key:"B",text:"Formulating a recommendation and submitting it to the approving authority"},{key:"C",text:"Cancelling the project"},{key:"D",text:"Transferring project responsibilities"}],answer:"B",explanation:"The final stage in the Impact Analysis process involves assembling the collected data and formulating a recommendation, which is then submitted to the approving authority, such as a Change Control Board (CCB).<br>"}]},null,8,["quizData"])])}const b=t(r,[["render",l]]);export{y as __pageData,b as default};
