import{_ as i,C as t,o,c as s,H as n,Q as a}from"./chunks/framework.QBCT-Upj.js";const b=JSON.parse('{"title":"Prioritization Schemes","description":"","frontmatter":{"prev":false,"next":false},"headers":[],"relativePath":"content/gist/business-analysis/tools-techniques/prioritization-schemes.md","filePath":"content/gist/business-analysis/tools-techniques/prioritization-schemes.md"}'),r={name:"content/gist/business-analysis/tools-techniques/prioritization-schemes.md"},l=a("",12);function h(u,c,d,m,p,g){const e=t("QuizJS");return o(),s("div",null,[l,n(e,{quizData:[{id:4698,question:'What is the purpose of the "Buy a Feature" prioritization scheme in Business Analysis?<br>',choices:[{key:"A",text:"To determine the most technologically advanced features"},{key:"B",text:"To enable stakeholders to agree on prioritization by 'buying' features with pretend money"},{key:"C",text:"To prioritize features based on their alignment with global market trends"},{key:"D",text:"To focus solely on features that reduce operational costs"}],answer:"B",explanation:'The "Buy a Feature" scheme is a collaborative game where stakeholders use pretend money to buy their choice of features. The features that receive the most money are considered most valuable and are prioritized accordingly.<br>'},{id:4699,question:"How does the Delphi method aid in prioritization?<br>",choices:[{key:"A",text:"By using a consensus-building technique involving anonymous input from subject matter experts"},{key:"B",text:"By focusing on the personal preferences of the project manager"},{key:"C",text:"By prioritizing based on the latest technological trends"},{key:"D",text:"By determining priorities based on financial investments alone"}],answer:"A",explanation:"The Delphi method is a consensus-building technique that uses anonymous input from subject matter experts and multiple rounds of discussion to reach agreement on prioritization, aiming to reduce peer pressure and groupthink.<br>"},{id:4700,question:"What is the key focus of the Minimum Viable Product (MVP) prioritization mechanism?<br>",choices:[{key:"A",text:"To define the scope of the first release by identifying the minimum number of features for customer value"},{key:"B",text:"To prioritize features solely based on their financial returns"},{key:"C",text:"To focus on the most complex features of the solution"},{key:"D",text:"To prioritize features based on the ease of implementation"}],answer:"A",explanation:"The MVP prioritization mechanism is used to define the scope of the first release of a solution by identifying the fewest number of features that would constitute a solution from which the customer would obtain value.<br>"},{id:4701,question:"What does the MoSCoW technique involve in prioritization?<br>",choices:[{key:"A",text:"Categorizing each requirement into groups like Must have, Should have, Could have, or Won’t have"},{key:"B",text:"Prioritizing based solely on the highest financial return"},{key:"C",text:"Focusing on requirements that align with the personal goals of the stakeholders"},{key:"D",text:"Considering only the technical aspects of the requirements"}],answer:"A",explanation:"The MoSCoW technique categorizes each requirement into one of the following groups: Must have, Should have, Could have, or Won’t have, aiding in prioritization based on the level of necessity and impact.<br>"},{id:4702,question:"What is the principle behind the Weighted Shortest Job First (WSJF) method?<br>",choices:[{key:"A",text:"To prioritize user stories based on dimensions like business value, time criticality, risk reduction, and effort"},{key:"B",text:"To focus on prioritizing the longest and most complex jobs"},{key:"C",text:"To prioritize jobs based on their alignment with the CEO's vision"},{key:"D",text:"To rank jobs solely based on their operational impact"}],answer:"A",explanation:"Weighted Shortest Job First (WSJF) is a method used to rank user stories based on multiple dimensions including business value, time criticality, risk reduction or opportunity enablement, and effort, using a formula to calculate a weighted value for each story.<br>"}]},null,8,["quizData"])])}const y=i(r,[["render",h]]);export{b as __pageData,y as default};
